ctx = bytes.fromhex("1c781d9ac91004a4a0502757fec35e4a")
rnd = bytes.fromhex("6dbbc4375136df3b07f7c70e639e223e177e7fd53b161b3f4d57791794f12624")

sig = ml_dsa.sign(sk, m, ctx, rnd)

print("# sig: ", sig.hex())